mapper.select:
SELECT * FROM wifiinfo WHERE date BETWEEN #{from} AND #{to}

    AND pos_x BETWEEN #{pos_x} AND #{pos_x}+1
    AND pos_y BETWEEN #{pos_y} AND #{pos_y}+1


    AND building = #{building}


    AND SSID = #{SSID}

mapper.delete:
DELETE FROM wifiinfo
WHERE date BETWEEN #{from} AND #{to}

    AND building = #{building}


    AND SSID = #{SSID}


    AND uuid = #{uuid}
